mapper.select:

SELECT privilege 
  FROM user_privilege
 WHERE user_id = #{id}

mapper.update:

UPDATE user SET
       password = #{password}
 WHERE user_name = #{username}


UPDATE user SET
       password = #{password}
 WHERE password_reset_id = #{resetId}

mapper.select:

SELECT CASE WHEN count(*) > 0 THEN 1 ELSE 0 END 
  FROM user u 
 WHERE u.user_name =  #{userName}
   AND u.password = #{password}


SELECT count(*)
  FROM user u 
 WHERE u.user_name =  #{userName}


SELECT count(*)
  FROM user u 
 WHERE u.email =  #{email}
